import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {props.value}
    </button>
  );
}
function AddUser(props) {
  return (
    <button className="button" onClick={props.onClick}>
      Agregar
    </button>
  );
}

class Board extends React.Component {
  renderSquare(i) {
    return (
      <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  renderAddUser() {
    return (
      <AddUser
        value={"agregar"}
        onClick={() => this.props.onClick()}
      />
    );
  }

  render() {
    return (
      <div>
	<div>
	  <div className="board-row">
	    <b>Nombre</b>
	  </div>
	  <div>
	    <input name="Nombre"/>
	  </div>
	</div>
	<div>
	  <div className="board-row">
	    <b>Apellido</b>
	  </div>
	  <div>
	    <input name="Apellido"/>
	  </div>
	</div>
	<div>
	  <div className="board-row">
	    <b>Origen</b>
	  </div>
	  <div>
	    <input name="Origen"/>
	  </div>
	</div>
	<div>
	  <div className="board-row">
	    <b>Destino</b>
	  </div>
	  <div>
	    <input name="Destino"/>
	  </div>
	</div>
	<div>
          <div>
            {this.renderAddUser()}
	  </div>
        </div>
      </div>
    );
  }
}

class AeroLine extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [
        {
          squares: Array(3).fill(null)
        }
      ],
      stepNumber: 0,
      xIsNext: true
    };
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? "X" : "O";
    this.setState({
      history: history.concat([
        {
          squares: squares
        }
      ]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0
    });
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);

    const moves = history.map((step, move) => {
      const desc = move ?
        'Go to move #' + move :
        'Go to AeroLine start';
      return (
        <li key={move}>
          <button onClick={() => this.jumpTo(move)}>{desc}</button>
        </li>
      );
    });

    let status;
    if (winner) {
      status = "Winner: " + winner;
    } else {
      status = "Next player: " + (this.state.xIsNext ? "X" : "O");
    }

    return (
      <div className="aeroline">
        <div className="aeroline-board">
          <Board
            squares={current.squares}
            onClick={i => this.handleClick(i)}
          />
        </div>
        <div className="aeroline-info">
          <div>{status}</div>
          <ol>{moves}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(<AeroLine />, document.getElementById("root"));

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
